pm.test("[POST]::/crm/leads - Response status code is 422", function () {
    pm.expect(pm.response.code).to.equal(422);
});
// Validate if response header has matching content-type
pm.test("[POST]::/crm/leads - Content-Type is application/json", function () {
   pm.expect(pm.response.headers.get("Content-Type")).to.include("application/json");
});
// Validate if response has JSON Body 
pm.test("[POST]::/crm/leads - Response has JSON Body", function () {
    pm.response.to.have.jsonBody();
});
// Response Validation
const schema = {"properties":{"status_code":{"type":"number","description":"HTTP status code","example":422},"error":{"type":"string","description":"Contains an explanation of the status_code as defined in HTTP/1.1 standard (RFC 7231)","example":"Unprocessable Entity"},"typeName":{"type":"string","description":"The type of error returned","example":"InvalidStateError"},"message":{"type":"string","description":"A human-readable message providing more details about the error.","example":"Invalid State"},"detail":{"type":"string","description":"Contains parameter or domain specific information related to the error and why it occured.","example":"State did not include unified_api"},"ref":{"type":"string","description":"Link to documentation of error type","example":"https://developers.apideck.com/errors#invalidstateerror"}},"type":"object"}

// Validate if response matches JSON schema 
pm.test("[POST]::/crm/leads - Schema is valid", function() {
    pm.response.to.have.jsonSchema(schema,{unknownFormats: ["int32", "int64"]});
});

