// Validate response status code 
pm.test("[POST]::/crm/leads - Response status code is 400", function () {
    pm.expect(pm.response.code).to.equal(400);
});
// Validate if response has JSON Body 
pm.test("[POST]::/crm/leads - Response has JSON Body", function () {
    pm.response.to.have.jsonBody();
});
// Response Validation
const schema = {"type":"object","required":["status_code","status","service","resource","operation","data"],"properties":{"status_code":{"type":"integer","description":"HTTP Response Status Code","example":200},"status":{"type":"string","description":"HTTP Response Status","example":"OK"},"service":{"type":"string","description":"Apideck ID of service provider","example":"zoho-crm"},"resource":{"type":"string","description":"Unified API resource name","example":"companies"},"operation":{"type":"string","description":"Operation performed","example":"one"},"data":{"title":"UnifiedId","required":["id"],"properties":{"id":{"type":"string","readOnly":true,"example":"12345"}},"type":"object"}}}

// Validate if response matches JSON schema 
pm.test("[POST]::/crm/leads - Schema is valid", function() {
    pm.response.to.have.jsonSchema(schema,{unknownFormats: ["int32", "int64"]});
});
// Set response object as internal variable
let jsonData = pm.response.json();
// Response body should have property "resource"
pm.test("[POST]::/crm/leads - Content check if property 'resource' exists", function() {
   pm.expect((typeof jsonData.resource !== "undefined")).to.be.true;
});
// Response body should have value "leads" for "resource"
if (typeof jsonData.resource !== "undefined") {
pm.test("[POST]::/crm/leads - Content check if value for 'resource' matches 'leads'", function() {
  pm.expect(jsonData.resource).to.eql("leads");
})};

pm.test('say hello Portman', function(){ 
  console.log('Hello Portman')
});
// pm.collectionVariables - Set missingParams.id as variable for jsonData.data[0].id  
if (typeof jsonData.data[0].id !== "undefined") {
   pm.collectionVariables.set("missingParams.id", jsonData.data[0].id);
   console.log("- use {{missingParams.id}} as collection variable for value",jsonData.data[0].id);
};
